package src.com.leetcode.s1930;

import java.util.Arrays;
import java.util.HashMap;
import java.util.HashSet;

class Solution {
    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.countPalindromicSubsequence(""));
    }

    public int countPalindromicSubsequence(String s) {
        int[] first = new int[26];
        int[] last = new int[26];
        Arrays.fill(first, Integer.MAX_VALUE);
        Arrays.fill(last, Integer.MAX_VALUE);
        for (int i = 0; i < s.length(); i++) {
            int c = s.charAt(i) - 'a';
            first[c] = Math.min(i, first[c]);
            last[c] = i;
        }

        HashSet<Character> hashSet = new HashSet<>();
        int count = 0;
        for (int i = 0; i < first.length; i++) {
            if (first[i] != Integer.MAX_VALUE && last[i] != Integer.MAX_VALUE && last[i] - first[i] > 1) {
                hashSet.clear();
                for (int j = first[i] + 1; j < last[i]; j++) {
                    hashSet.add(s.charAt(j));
                }
                count += hashSet.size();
            }
        }
        return count;
    }
}
